class Record:
	def __init__(self, **attr):
		self.__dict__ = attr

	def getattr(self):
		return self.__dict__
	
	def __repr__(self):
		str = '<'
		sep = ''
		for key, value in self.getattr().items():
			str = str + sep + '%s=%s' % (key, value)
			sep = ', '

		return str + '>'
	

